import React ,{useEffect}from 'react'
import {Menu, Input,Button,Row, Col,Card,Avatar} from 'antd'
import Link from 'next/link'; 
import PropTypes from 'prop-types'; 
import LoginForm from './LoginForm'; 

import {useDispatch,useSelector, connect} from 'react-redux'
import { loginAction,logOutAction , LOG_IN, LOG_OUT} from '../reducers/user';


const AppLayout = ({children}) =>{

    const dispath = useDispatch(); //리덕스 액션 실행기 

    const {user,isLoggedIn} = useSelector(state=>state.user); 

    useEffect(()=>{
        // dispath({
        //     type:LOG_IN, 
        //     data:{
        //         nickname:'준일정',
        //     }
        // })
        console.log('user ==>>' , user); 
        dispath(loginAction); 

    },[]); 



    return(
            <div>
            <Menu mode="horizontal">    
                <Menu.Item key="home"><Link href="/"><a>노드버드</a></Link></Menu.Item>
                <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
                <Menu.Item key="mail">
                    <Input.Search enterButton style={{ verticalAlign: 'middle'}}/>
                </Menu.Item>
            </Menu>
            <Row gutter={8}>
                {/*xs:모바일, sm:작은화면, md:중간화면, lg:큰 화면*/}
                <Col xs={24} md={6}>
                   {isLoggedIn 
                   ?<Card 
                        actions={[
                            <div key="twit">짹짹<br />{3}</div>,
                            <div key="follwing">팔로잉<br />{user.Followings.length}</div>,
                            <div key="follower">팔로워<br />{user.Followers.length}</div>,
                                ]}
                      >              
                        <Card.Meta 
                            avatar={<Avatar>{user.nickname[0]}</Avatar>}
                            title={user.nickname}
                        />
                    </Card>
                    :
                    <LoginForm />}
                </Col>
                <Col xs={24} md={12}>
                    {children}
                </Col>
                <Col xs={24} md={6}>세번째</Col>
            </Row>
   
            </div>
    ); 
}

AppLayout.propTypes ={
    children : PropTypes.elementType,
}


export default AppLayout;
