const dummyUser = {
    nickname:'주닐정',
    Post:[],
    Followings:[],
    Followers:[],
};
//------------------------------------ state
export const initialState = {

    isLoggedIn : false,     
    user:null,

}; 

//------------------------------------


//------------------------------------ setState
export const LOG_IN = 'LOG_IN'; //액션의 이름 
export const LOG_OUT = 'LOG_OUT'; 

export const loginAction = {
    type:LOG_IN,
}

export const logOutAction = {
    type:LOG_OUT,
}


const reducer = (state = initialState , action)=>{

    switch(action.type){
        
        case LOG_IN:{
            return {
                ...state,
                isLoggedIn:true,
                user:dummyUser,
            };
        }
        case LOG_OUT:{
            return {
                ...state,
                isLoggedIn:false,
                user:null,
            }
        }

        default : {
            return {
                ...state,
            }
        }

    }


};

//------------------------------------.




export default reducer;
