import react,{useEffect} from 'react'
import {Card,Avatar} from 'antd'

import {useSelector} from 'react-redux'
const dummy = {
    nickname: '정준일', 
    Post: [],
    Followings:[],
    Followers:[],
    isLoggedIn: false, 

}

const UserProfile = ()=>{
    const {users} = useSelector(state=>state.user); 
            return(
                <Card 
                    actions={[
                        <div key="twit">짹짹<br />{users.user.Posts.length}</div>,
                        <div key="follwing">팔로잉<br />{users.user.Followings.length}</div>,
                        <div key="follower">팔로워<br />{users.user.Followers.length}</div>,
                            ]}
                 >              
                <Card.Meta 
                    avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
                    title={dummy.nickname}
                />
            </Card>
        );
}
console.log("==="+UserProfile);
export default UserProfile;
